sys_variable_value.value:
(function execute(inputs, outputs) {
  var audiences = [];
  if (inputs.status == '200') {
    var payload = JSON.parse(inputs.responseBody)
    for(var i=0; i<payload.lists.length; i++){
      var listObject = payload.lists[i];
      var audience = {'label': listObject.name, 'name': listObject.id}; 
      audiences.push(audience);  
    }
  }
  audiences.sort(function(a, b){ return a.label < b.label ? -1: 1 });  
  outputs.audiences = { data: audiences };         
})(inputs, outputs);

